import React, { useState } from "react";
import "./App.css";

function App() {
  const defaultPeople = [
    {
      id: 15,
      firstName: "John",
      lastName: "Lewis",
    },
    {
      id: 25,
      firstName: "Tom",
      lastName: "Hanks",
    },
    {
      id: 45,
      firstName: "Melissa",
      lastName: "Rauch",
    },
    {
      id: 87,
      firstName: "Laura",
      lastName: "Spencer",
    },
  ];

  const [people, setPeople] = useState(defaultPeople);
  const [newPerson, setNewPerson] = useState("");

  const createPerson = (firstName, lastName) => {
    if (firstName === "") {
      return;
    } else if (lastName === "") {
      return;
    }

    const newAddedPerson = {
      id: Math.random(),
      firstName: firstName,
      lastName: lastName,
    };
    console.log(newAddedPerson);

    const newArrayWithPersons = [...people, newAddedPerson];

    setPeople(newArrayWithPersons);
  };

  const handleClickAddPerson = (e) => {
    e.preventDefault();
    createPerson(newPerson);
    setNewPerson("");
  };

  return (
    <div className="App">
      {people.map((person) => (
        <div>
          <ul>
            <li>
              {person.firstName} {person.lastName}
            </li>
            <li>{createPerson}</li>
          </ul>
        </div>
      ))}
      <input
        type="text"
        onChange={(e) => setNewPerson(e.target.value)}
        value={newPerson}
      />
      <button onClick={handleClickAddPerson}>Add New Person</button>
    </div>
  );
}

export default App;
