import React, { useState } from "react";
import "./App.css";

function App() {
  const defaultPeople = [
    {
      firstName: "John",
      lastName: "Lewis",
    },
    {
      firstName: "Tom",
      lastName: "Hanks",
    },
    {
      firstName: "Melissa",
      lastName: "Rauch",
    },
    {
      firstName: "Laura",
      lastName: "Spencer",
    },
  ];

  const [people, setPeople] = useState(defaultPeople);
  const [newPerson, setNewPerson] = useState("");

  const createPerson = (firstName) => {
    if (firstName === "") {
      return;
    }
  };

  const handleClickAddPerson = (e) => {
    e.preventDefault();
    createPerson(newPerson);
    setNewPerson("");
  };
  return (
    <div className="App">
      {people.map((person) => (
        <div>
          <ul>
            <li>
              {person.firstName} {person.lastName}
            </li>
          </ul>
        </div>
      ))}
      <input type="text" />
      <button onClick={handleClickAddPerson}>Add New Person</button>
    </div>
  );
}

export default App;
