import React, { useState } from "react";
import Table from "./Table";
import FormAddPeople from "./Form";
import "./App.css";

function App({ valuesNewPerson, setValuesNewPerson }) {
  const defaultPeople = [
    {
      id: 15,
      firstName: "John",
      lastName: "Lewis",
    },
    {
      id: 25,
      firstName: "Tom",
      lastName: "Hanks",
    },
    {
      id: 45,
      firstName: "Melissa",
      lastName: "Rauch",
    },
    {
      id: 87,
      firstName: "Laura",
      lastName: "Spencer",
    },
  ];

  const [people, setPeople] = useState(defaultPeople);

  const createPerson = (firstName, lastName) => {
    if (firstName === "") {
      return;
    } else if (lastName === "") {
      return;
    }

    const newAddedPerson = {
      id: parseInt(Math.random() * 10000),
      firstName: valuesNewPerson.firstName,
      lastName: valuesNewPerson.lastName,
    };

    const newArrayWithPersons = [...people, newAddedPerson];

    setPeople(newArrayWithPersons);
  };

  const handleClickAddPerson = (e) => {
    e.preventDefault();
    createPerson(valuesNewPerson.firstName, valuesNewPerson.lastName);
    setValuesNewPerson({
      firstName: "",
      lastName: "",
    });
  };

  const handleRemovePerson = (id) => {
    const newArrayPersons = people.filter((person) => person.id !== id);
    setPeople(newArrayPersons);
  };

  return (
    <div className="App">
      <div className="container">
        <h2 className="title">People Already Here</h2>
        <Table people={people} handleRemovePerson={handleRemovePerson} />
        <FormAddPeople handleClickAddPerson={handleClickAddPerson} />
      </div>
    </div>
  );
}

export default App;
