import React, { useState } from "react";
import "./App.css";

function App() {
  const defaultPeople = [
    {
      id: 15,
      firstName: "John",
      lastName: "Lewis",
    },
    {
      id: 25,
      firstName: "Tom",
      lastName: "Hanks",
    },
    {
      id: 45,
      firstName: "Melissa",
      lastName: "Rauch",
    },
    {
      id: 87,
      firstName: "Laura",
      lastName: "Spencer",
    },
  ];

  const [people, setPeople] = useState(defaultPeople);
  const [valuesNewPerson, setValuesNewPerson] = useState({
    firstName: "",
    lastName: "",
  });

  const createPerson = (firstName, lastName) => {
    if (firstName === "") {
      return;
    } else if (lastName === "") {
      return;
    }

    const newAddedPerson = {
      id: Math.random() * 10000,
      firstName: valuesNewPerson.firstName,
      lastName: valuesNewPerson.lastName,
    };
    console.log(newAddedPerson);

    const newArrayWithPersons = [...people, newAddedPerson];

    setPeople(newArrayWithPersons);
  };

  const handleClickAddPerson = (e) => {
    e.preventDefault();
    createPerson(valuesNewPerson.firstName, valuesNewPerson.lastName);
    setValuesNewPerson({
      firstName: "",
      lastName: "",
    });
  };

  return (
    <div className="App">
      <div className="container">
        <h2 className="title">People Already Here</h2>
        <table>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
          </tr>
          <div className="persons-list">
            {people.map((person) => (
              <ul>
                <li>
                  {person.firstName} {person.lastName}
                </li>
                <li>{createPerson}</li>
              </ul>
            ))}
          </div>
        </table>
        <div className="container-form">
          <input
            type="text"
            placeholder="Your First Name"
            onChange={(e) =>
              setValuesNewPerson({
                ...valuesNewPerson,
                firstName: e.target.value,
              })
            }
            value={valuesNewPerson.firstName}
          />

          <input
            type="text"
            placeholder="Your Last Name"
            value={valuesNewPerson.lastName}
            onChange={(e) =>
              setValuesNewPerson({
                ...valuesNewPerson,
                lastName: e.target.value,
              })
            }
          />
          <button onClick={handleClickAddPerson}>Add New Person</button>
        </div>
      </div>
    </div>
  );
}

export default App;
