import React, { useState } from "react";
import Table from "./Table";
import ppp from "./data";
import "./App.css";

function App() {
  const [people, setPeople] = useState(ppp);

  const [valuesNewPerson, setValuesNewPerson] = useState({
    firstName: "",
    lastName: "",
  });

  const createPerson = (firstName, lastName) => {
    if (firstName === "") {
      return;
    } else if (lastName === "") {
      return;
    }

    const newAddedPerson = {
      id: parseInt(Math.random() * 10000),
      firstName: valuesNewPerson.firstName,
      lastName: valuesNewPerson.lastName,
    };
    console.log(newAddedPerson);

    const newArrayWithPersons = [...people, newAddedPerson];

    setPeople(newArrayWithPersons);
  };

  const handleClickAddPerson = (e) => {
    e.preventDefault();
    createPerson(valuesNewPerson.firstName, valuesNewPerson.lastName);
    setValuesNewPerson({
      firstName: "",
      lastName: "",
    });
  };

  const handleRemovePerson = (id) => {
    const newArrayPersons = people.filter((person) => person.id !== id);
    setPeople(newArrayPersons);
  };

  return (
    <div className="App">
      <div className="container">
        <h2 className="title">People Already Here</h2>
        <Table handleRemovePerson={handleRemovePerson} />
        <div className="container-form">
          <input
            type="text"
            placeholder="Your First Name"
            onChange={(e) =>
              setValuesNewPerson({
                ...valuesNewPerson,
                firstName: e.target.value,
              })
            }
            value={valuesNewPerson.firstName}
          />

          <input
            type="text"
            placeholder="Your Last Name"
            value={valuesNewPerson.lastName}
            onChange={(e) =>
              setValuesNewPerson({
                ...valuesNewPerson,
                lastName: e.target.value,
              })
            }
          />
          <button className="button add-button" onClick={handleClickAddPerson}>
            Add New Person
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
