import React, { useState } from "react";
import "./App.css";

function App() {
  const defaultPeople = [
    {
      id: 15,
      firstName: "John",
      lastName: "Lewis",
    },
    {
      id: 25,
      firstName: "Tom",
      lastName: "Hanks",
    },
    {
      id: 45,
      firstName: "Melissa",
      lastName: "Rauch",
    },
    {
      id: 87,
      firstName: "Laura",
      lastName: "Spencer",
    },
  ];

  const [people, setPeople] = useState(defaultPeople);
  const [valuesNewPerson, setValuesNewPerson] = useState({
    firstName: "",
    lastName: "",
  });
  const [activeRowId, setActiveRowId] = useState(null);

  const createPerson = (firstName, lastName) => {
    if (firstName === "") {
      return;
    } else if (lastName === "") {
      return;
    }

    const newAddedPerson = {
      id: parseInt(Math.random() * 10000),
      firstName: valuesNewPerson.firstName,
      lastName: valuesNewPerson.lastName,
    };
    console.log(newAddedPerson);

    const newArrayWithPersons = [...people, newAddedPerson];

    setPeople(newArrayWithPersons);
  };

  const handleClickAddPerson = (e) => {
    e.preventDefault();
    createPerson(valuesNewPerson.firstName, valuesNewPerson.lastName);
    setValuesNewPerson({
      firstName: "",
      lastName: "",
    });
  };

  const handleRemovePerson = (id) => {
    const newArrayPersons = people.filter((person) => person.id !== id);
    setPeople(newArrayPersons);
  };

  return (
    <div className="App">
      <div className="container">
        <h2 className="title">People Already Here</h2>
        <table className="styled-table">
          <thead>
            <tr>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Operation</th>
            </tr>
          </thead>
          <tbody>
            {people.map((person) => (
              <tr
                key={person.id}
                onMouseEnter={() => setActiveRowId(person.id)}
                onMouseLeave={() => setActiveRowId(null)}
                className={`${activeRowId === person.id ? "active-row" : ""}`}
              >
                <td>{person.firstName}</td>
                <td>{person.lastName}</td>

                <button
                  className={`delete-button ${
                    activeRowId === person.id ? "visible" : ""
                  }`}
                  onClick={() => handleRemovePerson(person.id)}
                >
                  Delete
                </button>
              </tr>
            ))}
            <tr>{createPerson}</tr>
          </tbody>
        </table>
        <div className="container-form">
          <input
            type="text"
            placeholder="Your First Name"
            onChange={(e) =>
              setValuesNewPerson({
                ...valuesNewPerson,
                firstName: e.target.value,
              })
            }
            value={valuesNewPerson.firstName}
          />

          <input
            type="text"
            placeholder="Your Last Name"
            value={valuesNewPerson.lastName}
            onChange={(e) =>
              setValuesNewPerson({
                ...valuesNewPerson,
                lastName: e.target.value,
              })
            }
          />
          <button className="button add-button" onClick={handleClickAddPerson}>
            Add New Person
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
